window.hugeArray=[];let isActive=!1,intervals=[];console.log("%cwindow.crypto.getRandomValues でCPUを使用し、その結果を格納してメモリーを使用します","font-size: 1.5em; color: dodgerblue;");for(let e=0;e<25;e++){const e=setInterval((()=>{if(isActive){const e=new Uint8Array(65536);crypto.getRandomValues(e);const t={timestamp:Date.now(),data:e};window.hugeArray.push(t)}}),10);intervals.push(e)}console.log(`%cActive intervals: ${intervals.length}`,"color:red; background-color:white; padding:0.5em; border-radius:1em;"),setInterval((()=>{isActive&&(document.querySelector("#length").innerText=`${window.hugeArray.length}\n推定 ${65536*window.hugeArray.length/1e9}GB`)}),100),document.querySelector("#check").addEventListener("change",(e=>{isActive=e.target.checked,console.log("%c"+(isActive?"Actived":"Inactived"),(isActive?"color:white; background-color:red;":"color:white; background-color:blue;")+" padding:0.1em;")}));